declare namespace UniCloudNamespace {
  interface Credentials {
    /**
     * 自定义登录私钥
     */
    private_key: string;
    /**
     * 自定义登录私钥ID
     */
    private_key_id: string;
  }

  interface LoggerOptions {
    /**
     * 以 log 日志等级输出日志
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-logger](https://uniapp.dcloud.io/uniCloud/cf-logger)
     */
    log(log: string): void;
    /**
     * 以 info 日志等级输出日志
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-logger](https://uniapp.dcloud.io/uniCloud/cf-logger)
     */
    info(info: string): void;
    /**
     * 以 warn 日志等级输出日志
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-logger](https://uniapp.dcloud.io/uniCloud/cf-logger)
     */
    warn(warn: string): void;
    /**
     * 以 error 日志等级输出日志
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-logger](https://uniapp.dcloud.io/uniCloud/cf-logger)
     */
    error(error: string): void;
  }

  interface UniCloudOptions {
    /**
     * 服务商
     */
    provider: string;
    /**
     * 服务空间id
     */
    spaceId: string;
    /**
     * 腾讯云自定义登录私钥内容
     */
    credentials?: Credentials;
  }

  interface UniCloudError {
    /**
     * 错误码
     */
    errCode: number | string;
    /**
     * 错误信息
     */
    errMsg: string;
    /**
     * 请求id方便排错
     */
    requestId?: string;
    /**
     * 错误详情
     */
    detail?: any;
  }

  interface CallFunctionOptions {
    /**
     * 云函数名
     */
    name: string | HBuilderX.CloudFunctionString;
    /**
     * 传递给云函数的参数
     */
    data?: any;
  }

  interface CallFunctionResult {
    /**
     * 状态码，操作成功则不返回
     */
    code: string;
    /**
     * 错误描述
     */
    message: string;
    /**
     * 云函数执行结果
     */
    result: string;
    /**
     * 请求序列号，用于错误排查
     */
    requestId: string;
  }

  interface UniError {
    /**
     * 错误模块名
     */
    errSubject?: string;
    /**
     * 错误码
     */
    errCode: number | string;
    /**
     * 错误信息
     */
    errMsg?: string;
    /**
     * 上级错误
     */
    cause?: UniError;
  }

  interface CloudInfo {
    provider: 'tencent' | 'aliyun';
    spaceId: string;
    functionType: 'cloudfunction' | 'cloudobject';
    functionName: string;
  }

  interface UniCloud {
    /**
     * 输出日志
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-logger](https://uniapp.dcloud.io/uniCloud/cf-logger)
     */
    logger: LoggerOptions;
    /** 服务空间初始化，返回uniCloud实例 */
    init(options: UniCloudOptions): UniCloud;
    /**
     * 调用云函数
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-functions?id=callbyfunction](https://uniapp.dcloud.io/uniCloud/cf-functions?id=callbyfunction)
     */
    callFunction(options: CallFunctionOptions): Promise<any>;
    /**
     * 调用云函数
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cloud-obj](https://uniapp.dcloud.io/uniCloud/cloud-obj)
     */
    importObject(objectName: string | HBuilderX.CloudObjectString): any;
    /**
     * 返回云端环境信息
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-functions.html#get-cloud-infos](https://uniapp.dcloud.io/uniCloud/cf-functions.html#get-cloud-infos)
     */
    getCloudInfos(): Array<CloudInfo>;
    /**
     * 返回当前云函数实例内正在处理的请求对应的客户端信息列表
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-functions.html#get-client-infos](https://uniapp.dcloud.io/uniCloud/cf-functions.html#get-client-infos)
     */
    getClientInfos(): Array<Record<string, any>>;
    /**
     * 返回当前云函数实例内正在处理的请求对应的客户端信息列表
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-functions.html#get-request-list](https://uniapp.dcloud.io/uniCloud/cf-functions.html#get-request-list)
     */
    getRequestList(): Array<string>;
  }
}
