declare namespace UniCloudNamespace {
  interface UserInfo {
    /**
     * 用户在uniCloud的唯一ID
     */
    uid: string;
    /**
     * 自定义登录的用户ID
     */
    customUserId: string;
  }

  interface CreateTicketOptions {
    /**
     * access_token的刷新时间
     */
    refresh: number;
    /**
     * access_token的过期时间
     */
    expire: number;
  }

  interface Auth {
    /**
     * 获取用户信息
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/authentication?id=cloud-get-user-info](https://uniapp.dcloud.io/uniCloud/authentication?id=cloud-get-user-info)
     */
    getUserInfo(): UserInfo;
    /**
     * 创建登录凭证
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/authentication?id=cloud-create-ticket](https://uniapp.dcloud.io/uniCloud/authentication?id=cloud-create-ticket)
     */
    createTicket(uid: string, options: CreateTicketOptions): string;
  }
  interface UniCloud {
    /**
     * 获取登录对象
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/authentication?id=cloud-custom-auth](https://uniapp.dcloud.io/uniCloud/authentication?id=cloud-custom-auth)
     */
    customAuth(): Auth;
  }
}
