declare namespace UniCloudNamespace {
  interface ProxyRequestResult {
    headers: Record<string, any>;
    body: any;
    statusCode: string;
    statusCodeValue: number;
  }

  interface HttpProxyForEip {
    /**
     * 代理发送Get请求
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-get](https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-get)
     */
    get(url: string, options?: Record<string, unknown>): Promise<ProxyRequestResult>;
    /**
     * 代理发送Post请求，form-urlencode格式
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-post-form](https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-post-form)
     */
    postForm(url: string, options?: Record<string, unknown>, headers?: Record<string, unknown>): Promise<ProxyRequestResult>;
    /**
     * 代理发送Post请求携带JSON格式数据
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-post-json](https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-post-json)
     */
    postJson(url: string, options?: Record<string, unknown>, headers?: Record<string, unknown>): Promise<ProxyRequestResult>;
    /**
     * 代理发送通用Post请求
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-post](https://uniapp.dcloud.net.cn/uniCloud/cf-functions.html#http-proxy-client-post)
     */
    post(url: string, text?: string, headers?: Record<string, unknown>): Promise<ProxyRequestResult>;
  }
  interface UniCloud {
    /**
     * 通过代理调用http服务
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-functions?id=httpclient](https://uniapp.dcloud.io/uniCloud/cf-functions?id=http-proxy-client)
     */
    httpProxyForEip: HttpProxyForEip;
  }
}
