declare namespace UniCloudNamespace {

  interface GetFrvManagerOptions {
    /**
     * 当前请求的requestId
     */
    requestId: string;
    /**
     * 在url化场景下无法获取appId，请自行传入
     */
    appId?: string;
  }

  interface GetCertifyIdOptions {
    /**
     * 真实姓名
     */
    realName: string;
    /**
     * 身份证号
     */
    idCard: string;
    /**
     * 客户端获取metaInfo接口返回的metaInfo信息
     */
    metaInfo: string;
    /**
     * 是否需要认证照片，默认不返回认证照片
     */
    needPicture: boolean;
  }

  interface GetCertifyIdResult {
    /**
     * 错误码
     */
    errCode: number;
    /**
     * 错误信息
     */
    errMsg: string;
    /**
     * 认证单据号，用于查询认证结果
     */
    certifyId: string;
  }

  interface GetAuthResultOptions {
    /**
     * 认证单据号
     */
    certifyId: string;
  }

  interface GetAuthResultResult {
    /**
     * 错误码
     */
    errCode: number;
    /**
     * 错误信息
     */
    errMsg: string;
    /**
     * 活体检测结果分数
     */
    score: number;
    /**
     * 人脸图片质量分
     */
    quality: number;
    /**
     * 实人认证状态，PROCESSING：初始化；SUCCESS：检测成功；FAIL：检测失败
     */
    authState: 'SUCCESS' | 'FAIL' | 'PROCESSING';
    /**
     * 实人认证照片url，临时链接需下载保存，请勿直接保存链接
     */
    pictureUrl?: string;
  }

  interface FrvManager {
    /**
     * 获取认证id
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/frv/dev.html#get-certify-id](https://uniapp.dcloud.net.cn/uniCloud/frv/dev.html#get-certify-id)
     */
    getCertifyId: (options: GetCertifyIdOptions) => GetCertifyIdResult;
    /**
     * 获取认证结果
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/frv/dev.html#get-auth-result](https://uniapp.dcloud.net.cn/uniCloud/frv/dev.html#get-auth-result)
     */
    getAuthResult: (options: GetAuthResultOptions) => GetAuthResultResult;
  }

  interface UniCloud {
    /**
     * 返回实人认证RpaManager实例
     *
     * 文档: [https://uniapp.dcloud.net.cn/uniCloud/frv/dev.html#get-frv-manager](https://uniapp.dcloud.net.cn/uniCloud/frv/dev.html#get-frv-manager)
     */
    getFacialRecognitionVerifyManager(options: GetFrvManagerOptions): FrvManager;
  }
}
