declare namespace UniCloudNamespace {
  interface UniCloudRequestOptions {
    /**
     * 服务器接口地址
     */
    url: string;
    /**
     * 请求的参数
     */
    data?: any;
    /**
     * 请求头
     */
    header?: Record<string, any>;
    /**
     * 请求方法
     */
    method?: 'GET' | 'POST' | 'DELETE' | 'PUT';
    /**
     * 超时时间
     */
    timeout?: number;
    /**
     * 如果设为 json，会对返回的数据进行一次 JSON.parse，非 json 不会进行 JSON.parse
     */
    dataType?: 'json';
    /**
     * 设置响应的数据类型。合法值：text、buffer
     */
    responseType?: 'text' | 'buffer';
    /**
     * 是否验证 ssl 证书
     */
    sslVerify?: boolean;
  }
  interface UniCloudRequestResult {
    /**
     * 状态码
     */
    statusCode?: string;
    /**
     * 响应头
     */
    header?: Record<string, any>;
    /**
     * 响应数据
     */
    data?: any;
  }

  interface UniCloud {
    /**
     * 调用http服务
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/cf-functions?id=unicloud-request](https://uniapp.dcloud.io/uniCloud/cf-functions?id=unicloud-request)
     */
    request(options: UniCloudRequestOptions): Promise<UniCloudRequestResult>;
  }
}
