declare namespace UniCloudNamespace {

  interface UploadFileOptions {
    /**
     * 文件的绝对路径，包含文件名。例如 foo/bar.jpg、foo/bar/baz.jpg 等
     */
    cloudPath: string;
    /**
     * buffer 或要上传的文件可读流
     */
    fileContent: any;
    /**
     * 阿里云是否以cloudPath作为实际存储路径，默认为false（否）
     */
    cloudPathAsRealPath?: boolean;
  }

  interface UploadFileResult {
    /**
     * 文件唯一 ID，用来访问文件，建议存储起来
     */
    fileID: string;
  }

  interface DownloadFileOptions {
    /**
     * 要下载的文件的 ID
     */
    fileID: string;
    /**
     * 下载的文件要存储的位置
     */
    tempFilePath: string;
  }

  interface DownloadFileResult {
    /**
     * 下载的文件的内容。如果传入 tempFilePath 则不返回该字段
     */
    fileContent: string;
  }

  interface FileListOptions {
    /**
     * 文件ID组成的数组
     */
    fileList: any[];
  }

  interface DeleteFileItem {
    /**
     * 云端fileID
     */
    fileID: string;
    /**
     * 状态码，操作成功则不返回
     */
    code: string;
  }

  interface DeleteFileResult {
    /**
     * 要删除的文件 ID 组成的数组
     */
    fileList: any[];
  }

  interface GetTempFileURLItem {
    /**
     * 文件 ID
     */
    fileID: string;
    /**
     * 状态码，操作成功则为 SUCCESS
     */
    code: string;
    /**
     * 文件访问链接
     */
    tempFileURL: string;
  }

  interface GetTempFileURLResult {
    /**
     * 存储下载链接的数组
     */
    fileList: any[];
  }

  interface GetFileInfoOptions {
    fileList: Array<string>;
  }

  interface GetFileInfoResultFileItem {
    /**
     * 文件ID
     */
    fileId: string;
    /**
     * 文件上传时间（秒）
     */
    gmtCreate: number;
    /**
     * 文件最近更改时间（秒）
     */
    gmtModified: number;
    /**
     * 文件原始名称
     */
    name: string;
    /**
     * 文件大小（Byte）
     */
    size: number;
    /**
     * 文件类型
     */
    type: string;
    /**
     * 文件CDN加速访问下载链接
     */
    url: string;

  }

  interface GetFileInfoResult {
    fileList: Array<GetFileInfoResultFileItem>;
  }

  interface UniCloud {
    /**
     * 上传文件到云端
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/storage?id=clouduploadfile](https://uniapp.dcloud.io/uniCloud/storage?id=clouduploadfile)
     */
    uploadFile(options: UploadFileOptions): Promise<any>;
    /**
     * 从云端下载文件
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/storage?id=clouddownloadfile](https://uniapp.dcloud.io/uniCloud/storage?id=clouddownloadfile)
     */
    downloadFile(options: DownloadFileOptions): Promise<any>;
    /**
     * 删除云端文件
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/storage?id=clouddeletefile](https://uniapp.dcloud.io/uniCloud/storage?id=clouddeletefile)
     */
    deleteFile(options: FileListOptions): Promise<any>;
    /**
     * 获取文件临时链接
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/storage?id=cloudgettempfileurl](https://uniapp.dcloud.io/uniCloud/storage?id=cloudgettempfileurl)
     */
    getTempFileURL(options: FileListOptions): Promise<any>;
    /**
     * 获取文件信息，阿里云专用
     *
     * 文档: [https://uniapp.dcloud.io/uniCloud/storage?id=cloud-get-file-info](https://uniapp.dcloud.io/uniCloud/storage?id=cloud-get-file-info)
     */
    getFileInfo(options: GetFileInfoOptions): Promise<GetFileInfoResult>;
  }
}
