        /**
         * 应用专属存储空间的外置存储空间根目录（caches/files）
         * @uniPlatform {
         *    "app": {
         *        "android": {
         *           "osVer": "5.0",
         *           "uniVer": "x",
         *           "unixVer": "3.99"
         *        },
         *        "ios": {
         *           "osVer": "12.0",
         *           "uniVer": "x",
         *           "unixVer": "4.11"
         *        }
         *    }
         * }
         */
